import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { removeFromCart } from '../utils/XMLUtils';

const CompFavList = () => {
    const [favItems, setFavItems] = useState([]);
    const URI = 'http://localhost:5000/LibrosMaldonado/Cart';
    const [fetched, setFetched] = useState(true)

    const getProduct = useCallback(async (cartIds) => {
        try {
            const idsQuery = cartIds.join(',');
            const res = await axios.get(`${URI}?ids=${idsQuery}`);
            if (res.data.length > 0) {
                setFavItems(res.data);
            }
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    }, [URI]);

    useEffect(() => {
        const fetchFavItems = async () => {
            // Recuperar el XML del localStorage
            const userSessionXML = localStorage.getItem('userSession');
            if (userSessionXML) {
                const parser = new DOMParser();
                const xmlDoc = parser.parseFromString(userSessionXML, 'text/xml');
                const favListElement = xmlDoc.getElementsByTagName('FavList')[0];
    
                // Convertir los ítems de la lista de favoritos en un array de objetos
                const favItems = favListElement ? Array.from(favListElement.getElementsByTagName('Item')).map(item => ({
                    IDLibro: parseInt(item.getElementsByTagName('ID')[0].textContent, 10),
                    Comentario: item.getElementsByTagName('Comentario')[0]?.textContent || '',
                })) : [];
    
                const favIds = favItems.map(item => item.IDLibro).filter(id => !isNaN(id));
    
                console.log('IDs de la lista de favoritos:', favIds); // Para depuración
    
                if (favIds.length > 0) {
                    try {
                        // Suponiendo que getProduct es una función que obtiene productos a partir de IDs
                        await getProduct(favIds);
    
                        // Guardar los ítems favoritos en el estado (o lo que corresponda)
                        setFavItems(favItems.reduce((acc, item) => {
                            acc[item.IDLibro] = { Comentario: item.Comentario };
                            return acc;
                        }, {}));
                    } catch (error) {
                        console.error('Error fetching favorite items:', error);
                    }
                }
            }
        };
    
        if (fetched) {
            setFetched(false);
            fetchFavItems();
        }
    }, [getProduct]);
    

    const remove = (itemId) => {
        removeFromCart(itemId);
        setFavItems(prevItems => prevItems.filter(item => item.IDLibro !== parseInt(itemId)));
    }

    return (
        <section className='list_section'> 
            <div>
                <h2>Carrito de Compras</h2>
            </div>
            <ul>
                {favItems.length > 0 ? (
                    <>
                        {favItems.map(book => (
                            <li key={book.IDLibro}>
                                <p className='list_name'>{book.Nombre}</p> 
                                <p className='list_price'><span className='black_span'>Precio:</span> ${book.Costo} <span className='black_span'>c/u</span></p>
                                <div className='list_opc'>
                                    <Link to={`/ProductSell/${book.IDLibro}`} className='dark_link list_link'>Ver producto</Link>
                                    <p onClick={() => remove(book.IDLibro)} className='list_opc_drop'>Eliminar</p>
                                </div>
                            </li>
                        ))}
                        <div className='list_pay_opc'>
                            <h2 className='final_cost'>Total: ${finalCost}</h2>
                            <button onClick={() => saveAndPay()}>Pagar</button>
                        </div>
                    </>
                ) : (
                    <li>El carrito está vacío.</li>
                )}
            </ul>
        </section>
    );
};

export default CompFavList;
