import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { downloadJSON, updateCartItem, deleteCartItem, getCartItemIds, getCart } from '../utils/JsonUtils';

const CompShopCart = () => {
    const [cartItems, setCartItems] = useState([]);
    const [quantities, setQuantities] = useState([]); 
    const [finalCost, setFinalCost] = useState(0);
    const URI = 'http://localhost:5000/LibMal/Libros';

    // Función para cargar los libros del carrito
    const loadCartItems = async () => {
        const currentCart = getCart();
        const ids = getCartItemIds(); 
        if (ids.length > 0) {
            try {
                const responses = await Promise.all(ids.map(id => axios.get(`${URI}/${id}`)));
                setCartItems(responses.map(response => response.data)); // Asume que la respuesta es el objeto libro
            } catch (error) {
                console.error('Error fetching books:', error);
            }
        } 
    };

    // Cálculo del costo final
    const calculateFinalCost = useCallback((items = cartItems) => {
        const total = items.reduce((sum, book) => {
            const quantity = quantities[book.IDLibro] || 1;
            return sum + (book.Costo * quantity);
        }, 0);
        setFinalCost(total);
    }, [cartItems, quantities]);

    // Cargar items del carrito al montar el componente
    useEffect(() => {
        loadCartItems(); // Cargar los items al iniciar
    }, []); // Solo al montar

    // Recalcular costo final cuando cartItems cambien
    useEffect(() => {
        calculateFinalCost(cartItems);
    }, [cartItems, calculateFinalCost]);

    return (
        <section className='list_section'> 
            <div>
                <h2>Carrito de Compras</h2>
            </div>
            <ul>
                {cartItems.length > 0 ? (
                    <>
                        {cartItems.map(book => (
                            <li key={book._id}>
                                <Link to={`/ProductSell/${book._id}`} className='dark_link list_link list_name'>{book.Nombre || 'Nombre no disponible'}</Link> 
                                <p className='list_price'><span className='black_span'>Precio:</span> ${book.Costo || 'N/A'}</p>
                                <p>
                                    <button className='list_cant_btn' onClick={() => updateCartItem(book._id, -1)}>&lt;</button>
                                    <span className='list_cant_dis'>{quantities[book._id] || 1}</span>
                                    <button className='list_cant_btn' onClick={() => updateCartItem(book._id, 1)}>&gt;</button>
                                </p>
                                <div className='list_opc'>
                                    <p onClick={() => deleteCartItem(book._id)} className='list_opc_drop'>A favoritos</p>
                                    <p onClick={() => deleteCartItem(book._id)} className='list_opc_drop'>Eliminar</p>
                                </div>
                            </li>
                        ))}
                        <div className='list_pay_opc'>
                            <h2 className='final_cost'>Total: ${finalCost}</h2>
                            <button onClick={downloadJSON}>Pagar</button>
                        </div>
                    </>
                ) : (
                    <li>El carrito está vacío.</li>
                )}
            </ul>
        </section>
    );
};

export default CompShopCart;
