import React, { useEffect, useState } from 'react';
import axios from 'axios';

const CompProductPage = ({ id }) => {
  const [product, setProduct] = useState(null);

  const URI = 'http://localhost:5000/LibrosMaldonado/Product/';

    useEffect(() => {
        getProductPage();
    }, []);

    const getProductPage = async () => {
        try {
            const res = await axios.get(`${URI}${id}`)
            if (res.data.length > 0) {
                setProduct(res.data)
            }
        } catch (error) {
            console.error('Error:', error);
        }
    }

    return (
        product.length > 0 ? (
            <article className='autor_selection' >
            <div className='section_name'>
                <h2>Autores</h2>
            </div>
            <section className='autor_wall'>
            {product.map((product) => (
                <PordSellObj
                    key={product.IDLibro}
                    Name={product.Nombre}
                    Autor={"autor"} 
                    Sinopsis={product.Sinopsis} 
                    ImgURL={product.IDLibro}
                />
            ))}
            </section>
            <button className='more_btn' onClick={seeMore}>Ver todos</button>
        </article>
        ) : <p>Producto no encontrado</p>
        
    )
}

export default CompProductPage;
