import axios from 'axios';
import { useState, useEffect } from 'react';
import { AutorObj } from '../../objects';

const URI = 'http://localhost:5000/LibMal/Libros/Autor/Books';

const AutorBooks = ({Name}) => {
    const [books, setBooks] = useState([]);

    useEffect(() => {
        getBookAutors();
    }, []);

    const getBookAutors = async () => {
        try {
            const res = await axios.get(`${URI}/${Name}`);
            if (res.data) {
                setBooks(res.data);
            }
        } catch (error) {
            console.error('Error:', error);
        }
    }

    return (
        books.length > 0 ? (
            <article className='autor_selection'>
                <section className='autor_wall'>
                    {books.map((autor) => (
                        <AutorObj
                            key={autor._id}
                            name={autor.Nombre}
                            imgURL={autor.URLImage}
                        />
                    ))}
                </section>
            </article>
        ) : (
            <section id='no_books'>
                <h1>Sin libros</h1>
            </section>
        )
    );
    
};

export default AutorBooks;
