import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { PordSellObj } from '../../objects';

const CompBookWall = ({ query }) => {
    const [books, setBooks] = useState([]);

    useEffect(() => {
        const fetchBooks = async () => {
            try {
                const res = await axios.get(`http://localhost:5000/LibMal/Libros/search${query}`);
                setBooks(res.data);
            } catch (error) {
                console.error('Error fetching books:', error);
            }
        };

        fetchBooks();
    }, [query]); 

    return (
        <div>
            {books.map((book) => (
                <PordSellObj
                    key={book._id}
                    id={book._id}
                    Name={book.Nombre}
                    Autor={book.Autor}
                    Costo={book.Costo}
                    Sinopsis={book.Sinopsis}
                    ImgURL={book.URLImagen}
                />
            ))}
        </div>
    );
};

export default CompBookWall;
