import axios from "axios";
import { useState } from "react";
import { useNavigate } from "react-router-dom";

const URI = 'http://localhost:5000/LibrosMaldonado/Register';

const FormCreateUser = ({ setUSer }) => {
    const navigate = useNavigate();

    const [name, setName] = useState("");
    const [nameError, setNameError] = useState(false);

    const [mail, setMail] = useState("");
    const [mailError, setMailError] = useState(false);

    const [pass, setPass] = useState("");
    const [passError, setPassError] = useState(false);

    const [keepSession, setKeepSession] = useState(false);



    const create = async () => {
        try {
            await axios.post(URI, { Nombre: name, Correo: mail, Contrasena: pass });
        } catch (e) {
            console.error("Error creating user:", e);
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (mail === "" || pass === "") {
            if (name === "") setNameError(true);
            if (mail === "") setMailError(true);
            if (pass === "") setPassError(true);
            return;
        } 
        setNameError(false);
        setMailError(false);
        setPassError(false);
        if (keepSession) {
            create();
            generateXML(mail)
            setUSer([mail])
            navigate('/');
        } else {
            create();
            navigate('/Login');
        }
    };

    const handleNameChange = (e) => {
        setName(e.target.value);
        if (e.target.value !== "") {
            setNameError(false);
        }
    };

    const handleMailChange = (e) => {
        setMail(e.target.value);
        if (e.target.value !== "") {
            setMailError(false);
        }
    };

    const handlePassChange = (e) => {
        setPass(e.target.value);
        if (e.target.value !== "") {
            setPassError(false);

        }
    };

    const generateXML = (user) => {
        return `
        <UserSession>
            <User>
                <Email>${user.Correo}</Email>
            </User>
            <Cart>
            </Cart>
            <FavList>
            </FavList>
        </UserSession>`;
    };

    return(
        <form className='form__reg_log' onSubmit={handleSubmit}>
            <h1>Registro</h1>
            <p>Nombre:</p>
            <input
                type="text"
                value={name}
                onChange={handleNameChange}
                onBlur={() => { if (name === "") setNameError(true); }}
            />
            {nameError && <sup>Error en el campo</sup>}
            <p>Correo:</p>
            <input
                type="email"
                value={mail}
                onChange={handleMailChange}
                onBlur={() => { if (mail === "") setMailError(true); }}
            />
            {mailError && <sup>Error en el campo</sup>}
            <p>Contraseña:</p>
            <input
                type="password"
                value={pass}
                onChange={handlePassChange}
                onBlur={() => { if (pass === "") setPassError(true); }}
            />
            {passError && <sup>Error en el campo</sup>}
            <p><input type="checkbox" onChange={(e) => setKeepSession(e.target.checked)}/> Iniciar sesión</p>
            <button type="submit" value="Enviar">Registrarse</button>
        </form>
    )
}

export default FormCreateUser
