import React, { useState } from 'react';
import './css/log_reg.css';
import fond from '../img/fond/loginfondo.png';

export default function Login() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
  });

  const [formErrors, setFormErrors] = useState({
    name: false,
    email: false,
    password: false,
  });
  
  const handleInputChange = (e) => {
    const { id, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [id]: value }));
  };

  const validateField = (field) => {
    setFormErrors((prevErrors) => ({
      ...prevErrors,
      [field]: formData[field].trim() === '',
    }));
  };

  // Manejar el evento de submit
  const handleSubmit = (e) => {
    e.preventDefault();

    const errors = {
      name: formData.name.trim() === '',
      email: formData.email.trim() === '',
      password: formData.password.trim() === '',
    };

    setFormErrors(errors);

    const isValid = !Object.values(errors).some((error) => error);
    if (isValid) {
      console.log('Formulario enviado:', formData);
    } else {
      console.log('Hay errores en el formulario');
    }
  };

  return (
    <main style={{ backgroundImage: `url(${fond})` }}>
      <form id="register" onSubmit={handleSubmit}>
        <h1>Registro</h1>

        <p>Nombre:</p>
        <input type="text" id="name" value={formData.name} onChange={handleInputChange} onBlur={() => validateField('name')}/>
        {formErrors.name && <sup>No deje vacío este campo.</sup>}
        <p>Correo:</p>
        <input type="email" id="email" value={formData.email} onChange={handleInputChange}onBlur={() => validateField('email')}/>
        {formErrors.email && <sup>No deje vacío este campo.</sup>}
        <p>Contraseña:</p>
        <input type="password" id="password"
          value={formData.password} onChange={handleInputChange} onBlur={() => validateField('password')}/>
        {formErrors.password && <sup>No deje vacío este campo.</sup>}
        <p><sup> <input type="checkbox" /></sup> Mantener la sesión iniciada</p>
        <input type="submit" value="Enviar" />
      </form>
    </main>
  );
}
