import axios from 'axios';
import { useState, useEffect } from 'react';
import { ProductObj } from '../objects';

const URI = 'http://localhost:5000/LibrosMaldonado/Bdes';

const CompShowDesBooks = () => {
    const [books, setBooks] = useState([]);
    const [hasDiscounts, setHasDiscounts] = useState(false);

    useEffect(() => {
        getDescountBooks();
    }, []);

    const getDescountBooks = async () => {
        try {
            const res = await axios.get(URI);
            const fetchedBooks = res.data;
            setBooks(fetchedBooks);

            // Verifica si hay al menos un libro con descuento mayor a 0
            const hasDiscounts = fetchedBooks.some(book => book.Descuento > 0);
            setHasDiscounts(hasDiscounts);
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        hasDiscounts ? (
            <section className='product'>
                {books.map((book) => (
                    <ProductObj
                        key={book.IDLibro}
                        imageLink={book.imageLink}
                        price={book.Costo}
                        descount={book.Descuento}
                        name={book.Nombre}
                        fullInfo={book.Sinopsis}
                        Writers=''
                        productId={book.IDLibro}
                        alt={book.Nombre}
                    />
                ))}
            </section>
        ) : null
    );
};

export default CompShowDesBooks;
