import React, { useState, useEffect } from 'react'; 
import { useParams } from 'react-router-dom';
import fond from '../img/fond/loginfondo.png';
import { TSDV } from './faqs';
import './css/faq.css';

export default function Faq() {
    const { sect } = useParams();
    const [show, setShow] = useState('');

    useEffect(() => {
        setShow(sect);
    }, []); 

    const handleSelllick = () => {
        setShow(prevShow => prevShow === 'TSDV' ? '' : 'TSDV');
    };

    return (
        <main id='main_faq' style={{ backgroundImage: `url(${fond})`}}>
            <section id='contents'>

                <article className='question' >
                    <p className='article_title'>
                        <span className='display_info' onClick={handleSelllick}>Términos del servicio de ventas</span>
                        <span>Última actualización: 18/10/2024</span>
                    </p>
                    {
                        show === 'TSDV' ? (
                            <>
                                <TSDV/>
                                <h1 onClick={handleSelllick} className='close'>Cerrar</h1>
                            </>

                        )
                        : null
                    }
                </article>
                
            </section>
        </main>
    );
}
