import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { downloadJSON, updateCartItem, deleteCartItem, getCartItemIds, getCart } from '../utils/JsonUtils';

const CompShopCart = () => {
    const [cartItems, setCartItems] = useState([]);
    const [finalCost, setFinalCost] = useState(0);
    const URI = 'http://localhost:5000/LibMal/Libros/JSON';

    // Funci칩n para cargar los libros del carrito
    const loadCartItems = async () => {
        const currentCart = getCart(); // Obtener los datos actuales del carrito
        const ids = currentCart.map(item => item.ID); // Obtener las ID del carrito
        const quantities = currentCart.map(item => item.cant); // Obtener las cantidades

        if (ids.length > 0) {
            try {
                const responses = await Promise.all(ids.map(id => axios.get(`${URI}/${id}`)));
                
                // Almacena los libros junto con sus cantidades
                const itemsWithQuantities = responses.map((response, index) => ({
                    ...response.data,
                    cant: quantities[index], // Agregar la cantidad correspondiente
                }));

                setCartItems(itemsWithQuantities); // Actualizar el estado con los items
            } catch (error) {
                console.error('Error fetching books:', error);
            }
        }
    };

    // C치lculo del costo final
    const calculateFinalCost = useCallback((items = cartItems) => {
        const total = items.reduce((sum, book) => {
            return sum + (book.Costo * book.cant); // Usa book.cant para calcular el costo
        }, 0);
        setFinalCost(total);
    }, [cartItems]);

    // Cargar items del carrito al montar el componente
    useEffect(() => {
        loadCartItems(); // Cargar los items al iniciar
    }, []); // Solo al montar

    // Recalcular costo final cuando cartItems cambien
    useEffect(() => {
        calculateFinalCost(cartItems);
    }, [cartItems, calculateFinalCost]);

    return (
        <section className='list_section'> 
            <div>
                <h2>Carrito de Compras</h2>
            </div>
            <ul>
                {cartItems.length > 0 ? (
                    <>
                        {cartItems.map(book => (
                            <li key={book.IDLibro}>
                                <Link to={`/ProductSell/${book.IDLibro}`} className='dark_link list_link list_name'>{book.Nombre || 'Nombre no disponible'}</Link> 
                                <p className='list_price'><span className='black_span'>Precio:</span> ${book.Costo || 'N/A'}</p>
                                <p>
                                    <button className='list_cant_btn' onClick={() => updateCartItem(book.IDLibro, -1)}>&lt;</button>
                                    <span className='list_cant_dis'>{book.cant}</span> {/* Mostrar la cantidad */}
                                    <button className='list_cant_btn' onClick={() => updateCartItem(book.IDLibro, 1)}>&gt;</button>
                                </p>
                                <div className='list_opc'>
                                    <p onClick={() => deleteCartItem(book.IDLibro)} className='list_opc_drop'>Eliminar</p>
                                </div>
                            </li>
                        ))}
                        <div className='list_pay_opc'>
                            <h2 className='final_cost'>Total: ${finalCost}</h2>
                            <button onClick={downloadJSON}>Pagar</button>
                        </div>
                    </>
                ) : (
                    <li>El carrito est치 vac칤o.</li>
                )}
            </ul>
            <button onClick={downloadJSON}>Pagar</button>
        </section>
    );
};

export default CompShopCart;
