import axios from 'axios';
import { useState, useEffect } from 'react';
import { ProductObj } from '../objects';
import { useNavigate } from 'react-router-dom';

const URI = 'http://localhost:5000/LibrosMaldonado/Bdes';

const CompShowAutors = () => {
    const navigate = useNavigate();
    const [autors, setAutor] = useState([]);
    const [hasDiscounts, setHasDiscounts] = useState(false);

    const seeMore = () => {
        navigate('/ProductWall');
    };

    useEffect(() => {
        getAutors();
    }, []);

    const getAutors = async () => {
        try {
            const res = await axios.get(URI)
            setAutor(res.data)
        } catch (error) {
            console.error('Error:', error);
        }
    }

    return (
        hasDiscounts ? (
            <article class="article_selection" id="selection_article">
                <div>
                    <h2>Descuentos</h2>
                </div>
                <section className='product'>
                    {autors.map((autor) => (
                        <ProductObj
                            imageLink={autor.imageLink}
                            price={autor.Costo}
                            descount={autor.Descuento}
                            name={autor.Nombre}
                            fullInfo={autor.Sinopsis}
                            Writers=''
                            productId={autor.IDLibro}
                            alt={autor.Nombre}
                        />
                    ))}
                </section>
                <button onClick={seeMore}>Ver todos</button>
            </article>
        ) : null
    );
};

export default CompShowAutors;
