import axios from 'axios';
import { useState, useEffect } from 'react';
import { ProductObj } from '../objects';
import { useNavigate } from 'react-router-dom';

const URI = 'http://localhost:5000/LibrosMaldonado/Bdes';

const CompShowAutors = () => {
    const navigate = useNavigate();
    const [books, setBooks] = useState([]);
    const [hasDiscounts, setHasDiscounts] = useState(false);

    const seeMore = () => {
        navigate('/ProductWall');
    };

    useEffect(() => {
        getAutors();
    }, []);

    const getAutors = async () => {
        try {
            const res = await axios.get(URI);
            const fetchedBooks = res.data;
            const booksWithDiscounts = fetchedBooks.filter(book => book.Descuento > 0);
            const hasDiscounts = booksWithDiscounts.length > 0;
            setHasDiscounts(hasDiscounts);
            setBooks(booksWithDiscounts);
            setHasDiscounts(hasDiscounts);
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        hasDiscounts ? (
            <article class="article_selection" id="selection_article">
                <div>
                    <h2>Descuentos</h2>
                </div>
                <section className='product'>
                    {books.map((book) => (
                        <ProductObj
                            imageLink={book.imageLink}
                            price={book.Costo}
                            descount={book.Descuento}
                            name={book.Nombre}
                            fullInfo={book.Sinopsis}
                            Writers=''
                            productId={book.IDLibro}
                            alt={book.Nombre}
                        />
                    ))}
                </section>
                <button onClick={seeMore}>Ver todos</button>
            </article>
        ) : null
    );
};

export default CompShowAutors;
