import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import LogoSvg from './logo/LogoSvg';
import { LogOptions, RegisterLogButton } from './user_options'
import { SerchBar } from '../forms';
import { TopNav, Navigation } from '../navigation';
import { useAuth } from '../backend/utils/AuthContext';
import './css/header.css';

export default function Header() {
    const {isLogged} = useAuth()
    const [lastScrollTop, setLastScrollTop] = useState(0);
    const [headerVisible, setHeaderVisible] = useState(true);

    const handleScroll = () => {
        const currentScrollTop = window.pageYOffset || document.documentElement.scrollTop;

        if (currentScrollTop > lastScrollTop) {
            setHeaderVisible(false);
        } else {
            setHeaderVisible(true);
        }
        setLastScrollTop(currentScrollTop <= 0 ? 0 : currentScrollTop); 
    };

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    });

    return (
        <header className={`header ${headerVisible ? 'header--visible' : 'header--hidden'}`}>
            <section id='top'>
                <figure id='upper_logo' >
                    <LogoSvg />
                </figure>
                <p><Link className='link' to={'/'} title='Inicio'>Libros Maldonado</Link> a su servicio desde 1968</p>
                <TopNav />
            </section>
            <div className='div__header'>
                <figure className='div_logo' id='down_logo'>
                    <LogoSvg />
                </figure>
                <div id='serch_bar'>
                    <SerchBar />
                </div>
                <div id='log_opc'>
                    {
                        isLogged 
                        ? <LogOptions />
                        : <RegisterLogButton /> 
                    }
                </div>
                <nav id='nav'>
                    <Navigation />
                </nav>
            </div>
        </header>
    );
}
