import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { downloadJSON, updateCartItem, deleteCartItem, getListItemIds } from '../utils/JsonUtils';


const CompShopCart = () => {
    const [cartItems, setCartItems] = useState([]);
    const URI = 'http://localhost:5000/LibMal/Libros/JSON';
    const [finalCost, setFinalCost] = useState(0);
    const [quantities, setQuantities] = useState([]); 

    const calculateFinalCost = useCallback((items = cartItems) => {
        const total = items.reduce((sum, book) => {
            const quantity = quantities[book.IDLibro] || 1;
            return sum + (book.Costo * quantity);
        }, 0);
        setFinalCost(total);
    }, [cartItems, quantities]); 

    const getProduct = useCallback(async () => {
        const cartIds = getListItemIds()
        console.log(cartIds)
        try {
            const idsQuery = cartIds.join(',');
            const res = await axios.get(`${URI}?ids=${idsQuery}`);
            if (res.data.length > 0) {
                setCartItems(res.data);
                calculateFinalCost(res.data);
            }
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    }, []);

    useEffect(() => {
        calculateFinalCost();
    }, [calculateFinalCost, quantities]);

    return (
        <section className='list_section'> 
            <div>
                <h2>Carrito de Compras</h2>
            </div>
            <ul>
                {cartItems.length > 0 ? (
                    <>
                        {cartItems.map(book => (
                            <li key={book.IDLibro}>
                                <Link to={`/ProductSell/${book.IDLibro}`} className='dark_link list_link list_name'>{book.Nombre || 'Nombre no disponible'}</Link> 
                                <p className='list_price'><span className='black_span'>Precio:</span> ${book.Costo || 'N/A'}</p>
                                <p>
                                    <button className='list_cant_btn' onClick={() => updateCartItem(book.IDLibro, -1)}>&lt;</button>
                                    <span className='list_cant_dis'>{quantities[book.IDLibro] || 1}</span>
                                    <button className='list_cant_btn' onClick={() => updateCartItem(book.IDLibro, 1)}>&gt;</button>
                                </p>
                                <div className='list_opc'>
                                    <p onClick={() => deleteCartItem(book.IDLibro)} className='list_opc_drop'>Eliminar</p>
                                </div>
                            </li>
                        ))}
                        <div className='list_pay_opc'>
                            <h2 className='final_cost'>Total: ${finalCost}</h2>
                            <button onClick={downloadJSON}>Pagar</button>
                        </div>
                    </>
                ) : (
                    <li>El carrito está vacío.</li>
                )}
            </ul>
            <button onClick={downloadJSON}>Pagar</button>

        </section>
    );
};

export default CompShopCart;
