import React, { useEffect, useState } from 'react';
import axios from 'axios';

const CompProductPage = ({ id }) => {
  const [product, setProduct] = useState(null);

  const URI = 'http://localhost:5000/LibrosMaldonado/Product/';

    useEffect(() => {
        getProductPage();
    }, []);

    const getProductPage = async () => {
        try {
            const res = await axios.get(`${URI}${id}`)
            if (res.data.length > 0) {
                setProduct(res.data)
            }
        } catch (error) {
            console.error('Error:', error);
        }
    }

    if (!product) {
        return <div>Loading...</div>; // Mostrar un mensaje de carga mientras se obtienen los datos
      }

    return (
        <div>
            <h1>{product.Nombre}</h1>
            <img src={product.imageUrl} alt={product.Nombre} />
            <p>{product.Sinopsis}</p>
            <p>Price: ${product.Costo}</p>
        </div>
    )
}

export default CompProductPage;
