import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { removeFromFavorites } from '../utils/XMLUtils';

const CompFavList = () => {
    const [favItems, setFavItems] = useState([]); // FavItems como array
    const URI = 'http://localhost:5000/LibrosMaldonado/Cart';
    const [fetched, setFetched] = useState(true);

    // Función para obtener los productos por IDs
    const getProduct = useCallback(async (cartIds) => {
        try {
            const idsQuery = cartIds.join(',');
            const res = await axios.get(`${URI}?ids=${idsQuery}`);
            if (res.data.length > 0) {
                setFavItems(res.data); // Aquí establecemos los productos directamente
            }
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    }, [URI]);

    // Función para obtener la lista de favoritos del XML
    useEffect(() => {
        const fetchFavItems = async () => {
            const userSessionXML = localStorage.getItem('userSession');
            if (userSessionXML) {
                const parser = new DOMParser();
                const xmlDoc = parser.parseFromString(userSessionXML, 'text/xml');
                const favListElement = xmlDoc.getElementsByTagName('FavList')[0];

                // Convertir los ítems de la lista de favoritos en un array de objetos
                const favItems = favListElement
                    ? Array.from(favListElement.getElementsByTagName('Item')).map(item => ({
                        IDLibro: parseInt(item.getElementsByTagName('ID')[0].textContent, 10),
                    }))
                    : [];

                const favIds = favItems.map(item => item.IDLibro).filter(id => !isNaN(id));

                console.log('IDs de la lista de favoritos:', favIds); // Para depuración

                if (favIds.length > 0) {
                    try {
                        await getProduct(favIds); // Obtener los productos a partir de los favIds
                    } catch (error) {
                        console.error('Error fetching favorite items:', error);
                    }
                }
            }
        };

        if (fetched) {
            setFetched(false);
            fetchFavItems();
        }
    }, [fetched, getProduct]);

    // Función para remover un ítem de favoritos
    const remove = (itemId) => {
        removeFromFavorites(itemId);
        setFavItems(prevItems => prevItems.filter(item => item.IDLibro !== parseInt(itemId)));
    }

    return (
        <section className='list_section'> 
            <div>
                <h2>Lista de Favoritos</h2>
            </div>
            <ul>
                {favItems.length > 0 ? (
                    <>
                        {favItems.map(book => (
                            <li key={book.IDLibro}>
                                <p className='list_name'>{book.Nombre || 'Nombre no disponible'}</p> 
                                <p className='list_price'><span className='black_span'>Precio:</span> ${book.Costo || 'N/A'} <span className='black_span'>c/u</span></p>
                                <div className='list_opc'>
                                    <Link to={`/ProductSell/${book.IDLibro}`} className='dark_link list_link'>Ver producto</Link>
                                    <p>Al carrito</p>
                                    <p onClick={() => remove(book.IDLibro)} className='list_opc_drop'>Eliminar</p>
                                </div>
                            </li>
                        ))}
                    </>
                ) : (
                    <li>La lista está vacía.</li>
                )}
            </ul>
        </section>
    );
};

export default CompFavList;
