import React, { useState } from 'react';
import './css/log_reg.css';
import fond from '../img/fond/loginfondo.png';

export default function Login() {
    const [mail, setMail] = useState("")
    const [mailError, setMailError] = useState(false)

    const [pass, setPass] = useState("")
    const [passError, setPassError] = useState(false)

    const [error, setError] = useState(false)

    const handleSubmit = (e) => {
        e.preventDefaul()

        if(mail === "" ||  pass === "") {
            setError(true)
            return
        }
    }

    const handleMailChange = (e) => {
        if (mail === "") {
            setMailError(true)
        }
    }

    const handlePassChange = (e) => {
        if (pass === "") {
            setPassError(true)
        }
    }

    return (
        <main style={{ backgroundImage: `url(${fond})` }}>
            <form id="register" onSubmit={handleSubmit}>
                <h1>Inicio de sesión</h1>
                <p>Correo:</p>
                <input type="email" id="email" value={mail} onBlur={handleMailChange}/>
                {passError && <sup>Error en el campo</sup>}
                <p>Contraseña:</p>
                <input type="password" id="password" value={pass} onBlur={handlePassChange}/>
                <p><sup> <input type="checkbox" /></sup> Mantener la sesión iniciada</p>
                <button type="submit" value="Enviar" />
            </form>
            {error && <p>Campos erroneos</p>}
        </main>
    );
}
