import React, { useState } from 'react';
import './css/log_reg.css';
import fond from '../img/fond/loginfondo.png';
//import './RegisterUSR.css'

function Register(){
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        password: '',
    });

    const [formErrors, setFormErrors] = useState({
        name: false,
        email: false,
        password: false,
    });
    
    const handleInputChange = (e) => {
        const { id, value } = e.target;
        setFormData((prevData) => ({ ...prevData, [id]: value }));
    };

    const validateField = (field) => {
        setFormErrors((prevErrors) => ({
            ...prevErrors,
            [field]: formData[field].trim() === '',
        }));
    };
    return(
        <main style={{ backgroundImage: `url(${fond})` }}>
            <form className='.form__reg_log'>
                <h1>Registro</h1>
                <p>Nombre:</p>
                <input type="text" id="name" value={formData.name} onChange={handleInputChange} onBlur={() => validateField('name')}/>
                {formErrors.name && <sup>No deje vacío este campo.</sup>}
                <p>Correo:</p>
                <input type="email" id="email" value={formData.email} onChange={handleInputChange}onBlur={() => validateField('email')}/>
                {formErrors.email && <sup>No deje vacío este campo.</sup>}
                <p>Contraseña:</p>
                <input type="password" id="password" value={formData.password} onChange={handleInputChange} onBlur={() => validateField('password')}/>
                {formErrors.password && <sup>No deje vacío este campo.</sup>}
                <p><sup> <input type="checkbox" /></sup> Mantener la sesión iniciada</p>
                <button type="submit" value="Enviar" >Registrarse</button>
            </form>
        </main>
    )
}
export default Register
