import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const CompShopCart = () => {
    const [cartItems, setCartItems] = useState([])
    const URI = 'http://localhost:5000/LibrosMaldonado/Cart'; 
    const [finalCost, setFinalCost] = useState(0)
    const [cant, setCant] = useState({})

    const removeFromCart = (itemId) => {
        // Recuperar el XML del localStorage
        const userSessionXML = localStorage.getItem('userSession');
        if (userSessionXML) {
            // Parsear el XML
            const parser = new DOMParser();
            const xmlDoc = parser.parseFromString(userSessionXML, 'text/xml');
            
            // Encontrar el elemento del carrito
            const cartElement = xmlDoc.getElementsByTagName('Cart')[0];
            if (cartElement) {
                // Encontrar el item por ID y eliminarlo
                const items = cartElement.getElementsByTagName('Item');
                for (let i = 0; i < items.length; i++) {
                    // Convertir ambos a string para asegurarnos que estamos comparando correctamente
                    if (items[i].textContent === String(itemId)) {
                        cartElement.removeChild(items[i]);
                        break;
                    }
                }
                
                // Convertir el XML actualizado de nuevo a string
                const serializer = new XMLSerializer();
                const updatedXML = serializer.serializeToString(xmlDoc);
                
                // Guardar el XML actualizado en localStorage
                localStorage.setItem('userSession', updatedXML);
                
                // Actualizar el estado de cartItems para reflejar los cambios en la UI
                setCartItems(prevItems => prevItems.filter(item => item.IDLibro !== parseInt(itemId)));
                calculateFinalCost();
            }
        }
    };
    
    

    useEffect(() => {
        const fetchCartItems = async () => {
            const userSessionXML = localStorage.getItem('userSession');
            if (userSessionXML) {
                const parser = new DOMParser();
                const xmlDoc = parser.parseFromString(userSessionXML, 'text/xml');
                const cartElement = xmlDoc.getElementsByTagName('Cart')[0];
                const cartIds = cartElement ? Array.from(cartElement.getElementsByTagName('Item')).map(item => item.textContent) : [];
                
                if (cartIds.length > 0) {
                    getProduct(cartIds);
                }
            }
        };

        fetchCartItems();
    }, []);

    const getProduct = async (cartIds) => {
        try {
            const idsQuery = cartIds.join(',');
            const res = await axios.get(`${URI}?ids=${idsQuery}`);
            if (res.data.length > 0) {
                setCartItems(res.data);
            }
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    const calculateFinalCost = (items = cartItems) => {
        const total = items.reduce((sum, book) => {
            const cant = cants[book.IDLibro] || 1; // Usa la cantidad guardada o 1 si no existe
            return sum + (book.Costo * cant);
        }, 0);
        setFinalCost(total);
    };

    const handleQuantityChange = (id, change) => {
        setQuantities(prev => {
            const newQty = (prev[id] || 1) + change;
            if (newQty < 1) return prev; // No permitir cantidades negativas
            return { ...prev, [id]: newQty };
        });
    };

    useEffect(() => {
        calculateFinalCost(); // Calcular el costo total cuando cambian las cantidades
    }, [quantities]);


    return (
        <section className='list_section'> 
            <div>
                <h2>Carrito de Compras</h2>
            </div>
            <ul>
                {cartItems.length > 0 ? (
                    <>
                        {cartItems.map(book => (
                            <li key={book.IDLibro}>
                                <p className='list_name'>{book.Nombre}</p> 
                                <p className='list_price'><span className='black_span'>Precio:</span> ${book.Costo} <span className='black_span'>c/u</span></p>
                                <p>
                                    <button className='list_cant_btn'>&lt;</button>
                                    <span className='list_cant_dis'>{cant}</span>
                                    <button className='list_cant_btn'>&gt;</button>
                                </p>
                                <div className='list_opc'>
                                    <Link to={`/ProductSell/${book.IDLibro}`} className='dark_link list_link'>Ver producto</Link>
                                    <p onClick={() => removeFromCart(book.IDLibro)} className='list_opc_drop'>Eliminar</p>
                                </div>
                            </li>
                        ))}
                        <div className='list_pay_opc'>
                            <h2>Tienda: ${finalCost}</h2>
                            <button>Pagar</button>
                        </div>
                    </>
                ) : (
                    <li>El carrito está vacío.</li>
                )}
            </ul>
        </section>
    )
};

export default CompShopCart;
