import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const CompShopCart = () => {
    const [cartItems, setCartItems] = useState([]);
    const URI = 'http://localhost:5000/LibrosMaldonado/Cart'; 
    const [finalCost, setFinalCost] = useState(0);
    const [quantities, setQuantities] = useState({}); 

    const removeFromCart = (itemId) => {
        const userSessionXML = localStorage.getItem('userSession');
        if (userSessionXML) {
            const parser = new DOMParser();
            const xmlDoc = parser.parseFromString(userSessionXML, 'text/xml');
            const cartElement = xmlDoc.getElementsByTagName('Cart')[0];
            if (cartElement) {
                const items = cartElement.getElementsByTagName('Item');
                for (let i = 0; i < items.length; i++) {
                    if (items[i].textContent === String(itemId)) {
                        cartElement.removeChild(items[i]);
                        break;
                    }
                }
                const serializer = new XMLSerializer();
                const updatedXML = serializer.serializeToString(xmlDoc);
                localStorage.setItem('userSession', updatedXML);
                
                setCartItems(prevItems => {
                    const updatedItems = prevItems.filter(item => item.IDLibro !== parseInt(itemId));
                    calculateFinalCost(updatedItems); 
                    return updatedItems;
                });
            }
        }
    }

    useEffect(() => {
        const fetchCartItems = async () => {
            const userSessionXML = localStorage.getItem('userSession');
            if (userSessionXML) {
                const parser = new DOMParser();
                const xmlDoc = parser.parseFromString(userSessionXML, 'text/xml');
                const cartElement = xmlDoc.getElementsByTagName('Cart')[0];
                const cartIds = cartElement ? Array.from(cartElement.getElementsByTagName('Item')).map(item => item.textContent) : [];

                if (cartIds.length > 0) {
                    await getProduct(cartIds);
                }
            }
        };

        fetchCartItems();
    }, [])

    const getProduct = async (cartIds) => {
        try {
            const idsQuery = cartIds.join(',');
            const res = await axios.get(`${URI}?ids=${idsQuery}`);
            if (res.data.length > 0) {
                setCartItems(res.data);
                calculateFinalCost(res.data); 
            }
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    }

    const calculateFinalCost = (items = cartItems) => {
        const total = items.reduce((sum, book) => {
            const quantity = quantities[book.IDLibro] || 1; 
            return sum + (book.Costo * quantity);
        }, 0);
        setFinalCost(total);
    }

    const handleQuantityChange = (id, change) => {
        setQuantities(prev => {
            const newQty = (prev[id] || 1) + change;
            if (newQty < 1) return prev; 
            return { ...prev, [id]: newQty };
        });
    }

    const handlePayment = () => {
        // Generar el XML
        const userSessionXML = localStorage.getItem('userSession');
        const parser = new DOMParser();
        const xmlDoc = parser.parseFromString(userSessionXML, 'text/xml');
        
        const paymentElement = xmlDoc.createElement("Payment");
        const cartElement = xmlDoc.getElementsByTagName("Cart")[0];

        cartItems.forEach(book => {
            const itemElement = xmlDoc.createElement("Item");
            itemElement.setAttribute("ID", book.IDLibro);
            
            const quantityElement = xmlDoc.createElement("Cantidad");
            quantityElement.textContent = quantities[book.IDLibro] || 1;
            itemElement.appendChild(quantityElement);

            const priceElement = xmlDoc.createElement("Precio");
            priceElement.textContent = book.Costo;
            itemElement.appendChild(priceElement);

            const totalCostElement = xmlDoc.createElement("CostoTotal");
            const quantity = quantities[book.IDLibro] || 1;
            totalCostElement.textContent = (book.Costo * quantity).toFixed(2);
            itemElement.appendChild(totalCostElement);

            paymentElement.appendChild(itemElement);
        });

        // Agregar Payment al documento XML
        cartElement.appendChild(paymentElement);
        
        // Serializar el XML y guardarlo en localStorage o redirigir
        const serializer = new XMLSerializer();
        const updatedXML = serializer.serializeToString(xmlDoc);
        localStorage.setItem('userSession', updatedXML);

        // Redirigir a la página de pago
        history.push('/payment'); // Asegúrate de tener una ruta definida para "/payment"
    };

    useEffect(() => {
        calculateFinalCost(); 
    }, [quantities]);

    return (
        <section className='list_section'> 
            <div>
                <h2>Carrito de Compras</h2>
            </div>
            <ul>
                {cartItems.length > 0 ? (
                    <>
                        {cartItems.map(book => (
                            <li key={book.IDLibro}>
                                <p className='list_name'>{book.Nombre}</p> 
                                <p className='list_price'><span className='black_span'>Precio:</span> ${book.Costo} <span className='black_span'>c/u</span></p>
                                <p>
                                    <button className='list_cant_btn' onClick={() => handleQuantityChange(book.IDLibro, -1)}>&lt;</button>
                                    <span className='list_cant_dis'>{quantities[book.IDLibro] || 1}</span>
                                    <button className='list_cant_btn' onClick={() => handleQuantityChange(book.IDLibro, 1)}>&gt;</button>
                                </p>
                                <div className='list_opc'>
                                    <Link to={`/ProductSell/${book.IDLibro}`} className='dark_link list_link'>Ver producto</Link>
                                    <p onClick={() => removeFromCart(book.IDLibro)} className='list_opc_drop'>Eliminar</p>
                                </div>
                            </li>
                        ))}
                        <div className='list_pay_opc'>
                            <h2>Tienda: ${finalCost.toFixed(2)}</h2>
                            <button onClick={handlePayment}>Pagar</button>
                        </div>
                    </>
                ) : (
                    <li>El carrito está vacío.</li>
                )}
            </ul>
        </section>
    )
}

export default CompShopCart;
