import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const CompShopCart = () => {
    const [cartItems, setCartItems] = useState([]);
    const URI = 'http://localhost:5000/LibrosMaldonado/Cart'; 
    const [finalCost, setFinalCost] = useState(0);
    const [quantities, setQuantities] = useState({}); 
    const [state, setState] = useState(true); 


    const removeFromCart = (itemId) => {
        const userSessionXML = localStorage.getItem('userSession');
        if (userSessionXML) {
            const parser = new DOMParser();
            const xmlDoc = parser.parseFromString(userSessionXML, 'text/xml');
            const cartElement = xmlDoc.getElementsByTagName('Cart')[0];
            if (cartElement) {
                const items = cartElement.getElementsByTagName('Item');
                for (let i = 0; i < items.length; i++) {
                    if (items[i].textContent === String(itemId)) {
                        cartElement.removeChild(items[i]);
                        break;
                    }
                }
                const serializer = new XMLSerializer();
                const updatedXML = serializer.serializeToString(xmlDoc);
                localStorage.setItem('userSession', updatedXML);
                setCartItems(prevItems => prevItems.filter(item => item.IDLibro !== parseInt(itemId)));
                calculateFinalCost(); // Recalcular el costo total al eliminar
            }
        }
    };

    useEffect(() => {
        const fetchCartItems = async () => {
            const userSessionXML = localStorage.getItem('userSession');
            if (userSessionXML) {
                const parser = new DOMParser();
                const xmlDoc = parser.parseFromString(userSessionXML, 'text/xml');
                const cartElement = xmlDoc.getElementsByTagName('Cart')[0];
                const cartIds = cartElement ? Array.from(cartElement.getElementsByTagName('Item')).map(item => item.textContent) : [];

                if (cartIds.length > 0) {
                    await getProduct(cartIds);
                }
            }
        };

        fetchCartItems();
    }, []);

    const getProduct = async (cartIds) => {
        try {
            const idsQuery = cartIds.join(',');
            const res = await axios.get(`${URI}?ids=${idsQuery}`);
            if (res.data.length > 0) {
                setCartItems(res.data);
                calculateFinalCost(res.data);
            }
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    const calculateFinalCost = (items = cartItems) => {
        const total = items.reduce((sum, book) => {
            const quantity = quantities[book.IDLibro] || 0;
            return sum + (book.Costo * quantity);
        }, 0);
        setFinalCost(total);
    };

    const handleQuantityChange = (id, change) => {
        setQuantities(prev => {
            const newQty = (prev[id] || 0) + change;
            if (newQty < 0) {
                addOrUpdateBook(id,newQty,finalCost)
                return prev;
            } 
            return { ...prev, [id]: newQty };
        });
    };

    useEffect(() => {
        calculateFinalCost();
    }, [quantities]);

    saveAndPay = () => {

    }

    const createUpdateChild = (IDLibro, Cant, finalCost) => {
        // Obtener la sesión de usuario del localStorage
        const userSessionXML = localStorage.getItem('userSession');
    
        // Si existe la sesión de usuario
        if (userSessionXML) {
            const parser = new DOMParser();
            const xmlDoc = parser.parseFromString(userSessionXML, 'text/xml');
    
            // Buscar el nodo del carrito
            let payElement = xmlDoc.getElementsByTagName('Pay')[0];
            if (!payElement) {
                // Si no existe el nodo 'Pay', crearlo
                payElement = xmlDoc.createElement('Pay');
                xmlDoc.documentElement.appendChild(payElement);
            }
    
            // Buscar si el libro ya existe en el carrito
            let libroElement = Array.from(payElement.getElementsByTagName('Libro')).find(libro => {
                return libro.getAttribute('ID') === String(IDLibro);
            });
    
            if (libroElement) {
                // Si el libro ya existe, actualizar la cantidad
                const cantidadElement = libroElement.getElementsByTagName('Cantidad')[0];
                cantidadElement.textContent = Cant;
            } else {
                // Si no existe, crearlo
                libroElement = xmlDoc.createElement('Libro');
                libroElement.setAttribute('ID', IDLibro);
                
                const cantidadElement = xmlDoc.createElement('Cantidad');
                cantidadElement.textContent = Cant;
    
                libroElement.appendChild(cantidadElement);
                payElement.appendChild(libroElement);
            }
    
            // Actualizar el total del costo
            let totalAmountElement = payElement.getElementsByTagName('TotalAmaunt')[0];
            if (!totalAmountElement) {
                totalAmountElement = xmlDoc.createElement('TotalAmaunt');
                payElement.appendChild(totalAmountElement);
            }
            totalAmountElement.textContent = finalCost;
    
            // Serializar el XML actualizado y almacenarlo nuevamente en localStorage
            const serializer = new XMLSerializer();
            const updatedXML = serializer.serializeToString(xmlDoc);
            localStorage.setItem('userSession', updatedXML);
    
            // Establecer el estado de la aplicación
            setState(false); // Esto dependerá de tu lógica para manejar el estado
        }
    };
    

    const createPayXML = () => {
        return `
        <Pay>
            <Libro>
            </Libro>
            <TotalAmaunt>
            </TotalAmaunt>
        </Pay>`;
    }

    return (
        <section className='list_section'> 
            <div>
                <h2>Carrito de Compras</h2>
            </div>
            <ul>
                {cartItems.length > 0 ? (
                    <>
                        {cartItems.map(book => (
                            <li key={book.IDLibro}>
                                <p className='list_name'>{book.Nombre}</p> 
                                <p className='list_price'><span className='black_span'>Precio:</span> ${book.Costo} <span className='black_span'>c/u</span></p>
                                <p>
                                    <button className='list_cant_btn' onClick={() => handleQuantityChange(book.IDLibro, -1)}>&lt;</button>
                                    <span className='list_cant_dis'>{quantities[book.IDLibro] || 0}</span>
                                    <button className='list_cant_btn' onClick={() => handleQuantityChange(book.IDLibro, 1)}>&gt;</button>
                                </p>
                                <div className='list_opc'>
                                    <Link to={`/ProductSell/${book.IDLibro}`} className='dark_link list_link'>Ver producto</Link>
                                    <p onClick={() => removeFromCart(book.IDLibro)} className='list_opc_drop'>Eliminar</p>
                                </div>
                            </li>
                        ))}
                        <div className='list_pay_opc'>
                            <h2>Tienda: ${finalCost}</h2>
                            <button onClick={saveAndPay()}>Pagar</button>
                        </div>
                    </>
                ) : (
                    <li>El carrito está vacío.</li>
                )}
            </ul>
        </section>
    );
};

export default CompShopCart;
