import React, { useState } from 'react'
import './css/pord_sell_obj.css'
import addcart from '../../img/svg/cartadd.svg'
import favlist from '../../img/svg/favlist.svg'
import { useAuth } from '../../backend/utils/AuthContext';
import { addToCart, addToList } from '../../backend/utils/JsonUtils.js';

export default function PordSellObj({id, Name, Autor, Sinopsis, Costo, ImgURL}) {
    const {isLogged} = useAuth()
    const [cant, setCant] = useState(1)
    const [see, setSee] = useState(false)

    const handleQuantityChange = (val) => {
        setCant(prevCant => Math.max(prevCant + val, 1)); 
    };

    const showMore = () => {
        setSee(!see)
    }

    return(
        <>
        <article className='product_wrapper'>
            <figcaption className='product_image'>
                <img src={ImgURL} alt={Name} />
            </figcaption>
            <section className='product_info'>
                <h2><span>Titulo:</span> {Name}</h2>
                <p><span>Escritor/es:</span> {Autor}</p>
                <h3><span>Precio:</span> ${Costo}</h3>
                <div className='product_option'>
                    {
                        isLogged ? (
                            <>
                                <div>
                                <button onClick={() => addToCart(id)} className='product_btn' id='product_buy'><img src={addcart} alt='Cart'/>Agregar al carrito</button>
                                    <button className='list_cant_btn' onClick={() => handleQuantityChange(-1)}>&lt;</button>
                                    <span className='list_cant_dis'>{cant}</span>
                                    <button className='list_cant_btn' onClick={() => handleQuantityChange(1)}>&gt;</button>
                                </div>
                                <button onClick={() => addToList(id)} className='product_btn' id='product_save'><img src={favlist} alt='Favoritos'/>Guardar</button>
                            </>
                        ) : <h3>Inicie sesi√≥n para realizar compras</h3>
                    }
                </div>
                <section>
                    <p><span>Sinopsis:</span></p>
                    <p>{Sinopsis}</p>
                </section>
            </section>
            
        </article>
        {
            see ? (
                <section className='see_true'>
                    <p><span>Sinopsis:</span></p>
                    <p>{Sinopsis}</p>
                </section>
            ) : (
                <section className='see_false'>
                    <h3 onClick={showMore()}>Ver la sinnopsis entera</h3>
                </section>
            )
        }
        
        </>
    )
}
