import axios from "axios";
import { useState } from "react";

const URI = 'http://localhost:5000/LibMal/Libros/search'; 

const FormBookFilter = () => {
    const [name, setName] = useState('');
    const [author, setAuthor] = useState('');
    const [minPrice, setMinPrice] = useState('');
    const [maxPrice, setMaxPrice] = useState('');
    const [discount, setDiscount] = useState(false);

    const handleSearch = async (e) => {
        e.preventDefault(); // Prevenir el comportamiento por defecto del formulario

        try {
            const params = {
                name,
                autor: author,
                costo: { min: minPrice, max: maxPrice }, // Cambia según cómo esperes recibir el rango de precios
                descuento: discount ? 1 : 0 // Si es true, pasar 1, si es false, 0
            };

            const res = await axios.get(URI, { params });
            console.log(res.data); // Maneja la respuesta como quieras

        } catch (error) {
            console.error('Error al buscar libros:', error);
        }
    };

    return (
        <form className='form_filter' onSubmit={handleSearch}>
            <p>Nombre:</p>
            <input
                className='text_filter'
                type='text'
                placeholder='Nombre'
                value={name}
                onChange={(e) => setName(e.target.value)}
            />
            <p>Autor:</p>
            <input
                className='text_filter'
                type='text'
                placeholder='Autor'
                value={author}
                onChange={(e) => setAuthor(e.target.value)}
            />
            <p>
                <input
                    type='checkbox'
                    checked={discount}
                    onChange={(e) => setDiscount(e.target.checked)}
                /> 
                En descuento
            </p>
            <p>Costo:</p>
            <input
                className='text_filter'
                type='number'
                placeholder='Minimo'
                value={minPrice}
                onChange={(e) => setMinPrice(e.target.value)}
            />
            <p/>
            <input
                className='text_filter'
                type='number'
                placeholder='Maximo'
                value={maxPrice}
                onChange={(e) => setMaxPrice(e.target.value)}
            />
            <input className='logout_btn serch_btn' type='submit' value='Buscar'/>
        </form>
    );
};

export default FormBookFilter;
