import React, { useState } from 'react';
import './css/log_reg.css';
import fond from '../img/fond/loginfondo.png';

export default function Login() {
    const [mail, setMail] = useState("")
    const [mailError, setMailError] = useState(false)

    const [pass, setPass] = useState("")
    const [passError, setPassError] = useState(false)

    const [error, setError] = useState(false)

    const handleSubmit = (e) => {
        e.preventDefault()
        if(mail === "" ||  pass === "") {
            setError(true)
            return
        }
        setError(false)
    }

    const handleMailChange = (e) => {
        e.preventDefault()
        if (mail === "") {
            setMailError(true)
        }
        setMailError(false)
    }

    const handlePassChange = (e) => {
        e.preventDefault()
        if (pass === "") {
            setPassError(true)
        }
        setPassError(false)
    }

    return (
        <main style={{ backgroundImage: `url(${fond})` }}>
            <form className="from__reg_log" onSubmit={handleSubmit}>
                <h1>Inicio de sesi칩n</h1>
                <p>Correo:</p>
                <input type="email" value={mail} onBlur={handleMailChange}/>
                {mailError && <sup>Error en el campo</sup>}
                <p>Contrase침a:</p>
                <input type="password" value={pass} onBlur={handlePassChange}/>
                {passError && <sup>Error en el campo</sup>}
                <p><sup> <input type="checkbox" /></sup> Mantener la sesi칩n iniciada</p>
                <button type="submit" value="Enviar" >Iniciar sesi칩n</button>
                {error && <sup className='error'>Campos erroneos</sup>}
            </form>
        </main>
    );
}
